import React, {useContext, useState} from "react"
import {Context} from '../Context'

function CartItem({item}) {
    const {removeImageFromCart} = useContext(Context)
    const [isHovered, setIsHovered] = useState(false)
    const trashClass = isHovered ? "ri-delete-bin-fill" : "ri-delete-bin-line"

    //const trashEl = document.getElementById('trash-can')
    function setTrashIcon() {
        console.log('hovered')
        setIsHovered(prevState => ({isHovered: !prevState}))
    }

    return ( 
        <div className="cart-item">
            <i className={trashClass} onMouseEnter={setTrashIcon} onMouseLeave={setTrashIcon} onClick={()=>removeImageFromCart(item)}></i>
            <img src={item.url} width="130px" />
            <p>$5.99</p>
        </div>
    )
}

export default CartItem
