import React, {useContext} from "react"
import {Context} from '../Context'

function CartItem({item}) {
    const {removeImageFromCart} = useContext(Context)

    //const trashEl = document.getElementById('trash-can')
    function setTrashIcon() {
        console.log('hovered')
        document.getElementById("trash-can").classList.add('ri-delete-bin-fill');
        document.getElementById("trash-can").classList.remove('ri-delete-bin-line');
    }

    return (
        <div className="cart-item">
            <i className="ri-delete-bin-line" id="trash-can" onMouseOver={()=>setTrashIcon()} onClick={()=>removeImageFromCart(item)}></i>
            <img src={item.url} width="130px" />
            <p>$5.99</p>
        </div>
    )
}

export default CartItem
