import React, {useState, useEffect} from "react"
const Context = React.createContext()

// 1. Add a toggleFavorite method to context. It should take an `id` parameter and update the array of 
// allPhotos by flipping the `isFavorited` property of the photo with the matching `id`
//     a. Have this function also console.log something so we know it's running correctly
//     b. Don't try to modify the individual image object only. Make sure to provide a whole new 
//      array to context with the one item with the matching `id` being changed.
// 2. Make it so clicking the heart icon on any given image runs this method

function ContextProvider({children}) {
    const [allPhotos, setAllPhotos] = useState([])
    const [isFavorite, setIsFavorite] = useState(false)

    useEffect(() => {
        const url = "https://raw.githubusercontent.com/bobziroll/scrimba-react-bootcamp-images/master/images.json"

        const fetchData = async () => {
            try {
              const response = await fetch(url)
              const json = await response.json()
             // console.log(json)
              setAllPhotos(json)
            } catch (error) {
              console.log("error", error)
            }
          }

          fetchData()
      }, [])

    function toggleFavorite(id) {
        const updatedArray = allPhotos.map(photo => {
            if(photo.id === id) {
                console.log(id)
                console.log(!photo.isFavorite)
               return {
                   ...photo,
                   isFavorite: !photo.isFavorite
               }
            }
            return photo
        })
    }

    return (
        <Context.Provider value={{allPhotos}, {toggleFavorite}}>
            {children}
        </Context.Provider>
    )
}

export {ContextProvider, Context}
